USE metadash;

DROP TABLE IF EXISTS data_source;
CREATE TABLE IF NOT EXISTS data_source (
data_source_abbrev VARCHAR (20) NULL, 
data_source_name VARCHAR (250) NOT NULL, 
data_source_url VARCHAR (400) NULL, 
description VARCHAR (800) NULL, 
restricted BIT NULL,
terms_of_use VARCHAR(500) NULL,
nmfldfordatsrc BIGINT (4) NULL, 
contracts VARCHAR (200) NULL, 
date_created DATETIME NULL, 
creator VARCHAR (20) NULL, 
last_updated DATETIME NULL, 
update_user VARCHAR (20) NULL, 
exclude BIT NULL, 
delete_row BIT NULL,
rowsource varchar(12) NULL, 
hubid CHAR (10) NULL, 
orgid CHAR(10) NULL,
projid CHAR(10) NULL,
comments VARCHAR (600) NULL,
id BIGINT (4) NOT NULL AUTO_INCREMENT, PRIMARY KEY (ID) );

